# Crear un diccionario vacío para guardar las palabras y sus frecuencias
frecuencias = {}

# Recorrer la cadena por espacios usando split ()
for palabra in cadena.split (" "):
  # Si la palabra está en el diccionario, incrementar su frecuencia
  if palabra in frecuencias:
    frecuencias [palabra] += 1
  # Si no, agregarla al diccionario con frecuencia 1
  else:
    frecuencias [palabra] = 1

# Crear una lista vacía para guardar las palabras y las cantidades
lista = []

# Recorrer el diccionario y agregar cada palabra y su cantidad a la lista
for palabra, cantidad in frecuencias.items ():
  lista.append (palabra + "" + str (cantidad))

# Crear un string que contenga todas las palabras y las cantidades separadas por una coma
string = "".join (lista)

# Imprimir el string
print (string)
